import { useEffect, useState, useMemo } from "react";
import { useRecoilValue } from "recoil";
import { currentUserState } from "../recoil/atoms";
import FavoriteCard from "../components/FavoriteCard.js";
import api from "../api/posts";

function Favorites() {
	const currentUser = useRecoilValue(currentUserState);
	const [favorites, setFavorites] = useState([]);
	const [priceFilter, setPriceFilter] = useState();


	useEffect(() => {
		getFavorites();
	}, []);

	const getFavorites = async () => {
		try {
			const response = await api.get("favorites", {
				headers: { Authorization: `Bearer ${currentUser.access_token}` },
				params: { id: currentUser.user },
			});
			setFavorites(response.data);
		} catch (error) {
			console.log(error);
		}
	};

	const getFilteredList = () => {
		if (!priceFilter) {
			return favorites;
		}
		return favorites.filter((fav) => fav.price === priceFilter);
	};
	const filteredList = useMemo(getFilteredList, [priceFilter, favorites]);

	const handleChange = (e) => {
		setPriceFilter(e.target.value);
	};

	const renderedFavorites = filteredList.map((favorite) => (
		<FavoriteCard key={favorite.id} favorite={favorite} />
	));

	// const handleSort = () => {
	// 	const sortedFavs = [...favorites].sort((a,b) => {
	// 		return a.first > b.first ? 1: -1
	// 	})
	// 	setFavorites(sortedFavs)
	// }
	return (
		<div>
			<h1>Favorite Businesses</h1>
			{/* <p>Sort by:</p>
			<button onClick={handleSort}>A-Z</button> */}
		<label> 
			Sort By:
			<select onChange={(e) => handleSort(e.target.value)}>
				<option value="none">Default</option>
				<option value="ascending">Alphabetically, A-Z</option>
				<option value="descending">Alphabetically, Z-A</option>
				<option value="high">Low to high</option>
				<option value="low">High to low</option>
			</select>
			</label>
			<label>
				Filter By:
				<select value={priceFilter} onChange={handleChange}>
					<option value="$">$</option>
					<option value="$$">$$</option>
					<option value="$$$">$$$</option>
					<option value={""}>all</option>
				</select>
			</label>
			<form>
				<input type="search" />
			</form>
			{renderedFavorites}
		</div>
	);
}

export default Favorites;
